# app.py
from flask import Flask, jsonify, request
from flask_cors import CORS
from unidecode import unidecode


app = Flask(__name__)
CORS(app)  # Habilita CORS para o front-end React

# Dados fictícios de exemplo
acomodacoes = [
    {
        "id": 1,
        "nome": "Apartamento Beira-Mar",
        "imagem": "data:image/jpeg;base64,",
        "preco_noite": 350,
        "localizacao": "Florianópolis, SC",
        "descricao": "Excelente apartamento com vista para o mar",
        "quartos": 3,
        "banheiros": 2
    },
    {
        "id": 2,
        "nome": "Chalé na Serra",
        "imagem": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRF7249Mif_JWblizPkcGfevZy9D-avqF4mLw&s",
        "preco_noite": 500,
        "localizacao": "Campos do Jordão, SP",
        "descricao": "Chalé aconchegante com lareira",
        "quartos": 2,
        "banheiros": 1
    },
    {
        "id": 3,
        "nome": "Casa na Praia",
        "imagem": "data:image/jpeg;base64,",
        "preco_noite": 400,
        "localizacao": "Florianópolis, SC",
        "descricao": "Casa familiar próxima à praia",
        "quartos": 4,
        "banheiros": 3
    }
]

@app.route('/acomodacoes', methods=['GET'])
def get_acomodacoes():
    cidade = request.args.get('cidade')

    if cidade:
        cidade = cidade.strip().lower()
        filtradas = []

        for acomodacao in acomodacoes:
            # Normaliza a cidade da acomodação (remove acentos e lowercase)
            cidade_acomodacao = acomodacao['localizacao'].split(',')[0].strip().lower()
            cidade_acomodacao = unidecode(cidade_acomodacao)  # Remove acentos

            if unidecode(cidade) == cidade_acomodacao:
                filtradas.append(acomodacao)

        return jsonify(filtradas)

    return jsonify(acomodacoes)

@app.route('/acomodacoes/<int:id>', methods=['GET'])
def get_acomodacao(id):
    acomodacao = None

    for a in acomodacoes:
        if a['id'] == id:  #Compara o id da acomodação com o id fornecido
            acomodacao = a
            break

    if acomodacao:
        return jsonify(acomodacao)
    else:
        return ('', 404)

if __name__ == '__main__':
    app.run(debug=True)